typedef byte u8;
typedef u64bit limb;
typedef limb felem[5];

typedef struct
   {
       limb* x;
       limb* z;
   } fmonty_pair_t;

typedef struct
   {
     fmonty_pair_t q;
     fmonty_pair_t q_dash;
     const limb* q_minus_q_dash;
   } fmonty_in_t;

